import React, { useState, useEffect, useRef } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import Editor from '@monaco-editor/react';
import io from 'socket.io-client';
import axios from 'axios';

const API_URL = 'http://localhost:5001';
const socket = io(API_URL);

const LANGUAGES = [
  { id: 'python', name: 'Python' },
  { id: 'javascript', name: 'JavaScript' },
  { id: 'java', name: 'Java' },
  { id: 'cpp', name: 'C++' },
  { id: 'csharp', name: 'C#' },
  { id: 'typescript', name: 'TypeScript' },
  { id: 'go', name: 'Go' },
  { id: 'rust', name: 'Rust' }
];

function Room() {
  const { roomId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [username] = useState(location.state?.username);
  const [code, setCode] = useState('');
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isMuted, setIsMuted] = useState(false);
  const [participants, setParticipants] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('python');
  const [isConnected, setIsConnected] = useState(false);
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState({});
  const messagesEndRef = useRef(null);
  const peerConnections = useRef({});

  useEffect(() => {
    if (!username) {
      navigate('/');
      return;
    }

    // Initialize WebRTC
    initializeWebRTC();

    // Socket.IO connection
    socket.on('connect', () => {
      setIsConnected(true);
      socket.emit('join', { room: roomId, username });
    });

    socket.on('disconnect', () => {
      setIsConnected(false);
    });

    socket.on('code_updated', (data) => {
      if (data.username !== username) {
        setCode(data.code);
      }
    });

    socket.on('language_updated', (data) => {
      if (data.username !== username) {
        setSelectedLanguage(data.language);
      }
    });

    socket.on('new_message', (data) => {
      setMessages((prev) => [...prev, data]);
    });

    socket.on('user_joined', (data) => {
      setParticipants((prev) => [...prev, data.username]);
      if (data.username !== username) {
        createPeerConnection(data.username);
      }
    });

    socket.on('user_left', (data) => {
      setParticipants((prev) => prev.filter((user) => user !== data.username));
      if (peerConnections.current[data.username]) {
        peerConnections.current[data.username].close();
        delete peerConnections.current[data.username];
        setRemoteStreams((prev) => {
          const newStreams = { ...prev };
          delete newStreams[data.username];
          return newStreams;
        });
      }
    });

    socket.on('webrtc_offer', async (data) => {
      if (data.from !== username) {
        const pc = await createPeerConnection(data.from);
        await pc.setRemoteDescription(new RTCSessionDescription(data.offer));
        const answer = await pc.createAnswer();
        await pc.setLocalDescription(answer);
        socket.emit('webrtc_answer', {
          to: data.from,
          answer: answer,
          room: roomId
        });
      }
    });

    socket.on('webrtc_answer', async (data) => {
      if (data.from !== username && peerConnections.current[data.from]) {
        await peerConnections.current[data.from].setRemoteDescription(
          new RTCSessionDescription(data.answer)
        );
      }
    });

    socket.on('webrtc_ice', async (data) => {
      if (data.from !== username && peerConnections.current[data.from]) {
        try {
          await peerConnections.current[data.from].addIceCandidate(
            new RTCIceCandidate(data.candidate)
          );
        } catch (e) {
          console.error('Error adding ICE candidate:', e);
        }
      }
    });

    return () => {
      socket.disconnect();
      if (localStream) {
        localStream.getTracks().forEach(track => track.stop());
      }
      Object.values(peerConnections.current).forEach(pc => pc.close());
    };
  }, [roomId, username, navigate]);

  const initializeWebRTC = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        audio: true,
        video: false
      });
      setLocalStream(stream);
    } catch (error) {
      console.error('Error accessing media devices:', error);
    }
  };

  const createPeerConnection = async (targetUsername) => {
    const pc = new RTCPeerConnection({
      iceServers: [
        { urls: 'stun:stun.l.google.com:19302' }
      ]
    });

    if (localStream) {
      localStream.getTracks().forEach(track => {
        pc.addTrack(track, localStream);
      });
    }

    pc.ontrack = (event) => {
      setRemoteStreams((prev) => ({
        ...prev,
        [targetUsername]: event.streams[0]
      }));
    };

    pc.onicecandidate = (event) => {
      if (event.candidate) {
        socket.emit('webrtc_ice', {
          to: targetUsername,
          candidate: event.candidate,
          room: roomId
        });
      }
    };

    peerConnections.current[targetUsername] = pc;
    return pc;
  };

  const handleCodeChange = (value) => {
    setCode(value);
    socket.emit('code_change', {
      room: roomId,
      code: value,
      username: username
    });
  };

  const handleLanguageChange = (language) => {
    setSelectedLanguage(language);
    socket.emit('language_change', {
      room: roomId,
      language: language,
      username: username
    });
  };

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (newMessage.trim()) {
      socket.emit('chat_message', {
        room: roomId,
        message: newMessage,
        username,
      });
      setNewMessage('');
    }
  };

  const handleRunCode = async () => {
    try {
      const response = await axios.post(`${API_URL}/execute-code`, {
        code,
        language: selectedLanguage,
      });
      setMessages((prev) => [
        ...prev,
        { username: 'System', message: `Output: ${response.data.output}` },
      ]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { username: 'System', message: `Error: ${error.response?.data?.error || 'Execution failed'}` },
      ]);
    }
  };

  const handleLeaveRoom = () => {
    socket.emit('leave', { room: roomId, username });
    navigate('/');
  };

  const toggleMute = () => {
    if (localStream) {
      localStream.getAudioTracks().forEach(track => {
        track.enabled = !track.enabled;
      });
      setIsMuted(!isMuted);
    }
  };

  return (
    <div className="flex flex-col h-screen bg-gray-100">
      <header className="bg-white shadow">
        <div className="px-4 py-4 mx-auto max-w-7xl sm:px-6 lg:px-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center">
              <h1 className="text-xl font-semibold text-gray-900">
                Room: {roomId}
              </h1>
              <span className="ml-4 text-sm text-gray-500">
                Username: {username}
              </span>
            </div>
            <button
              onClick={handleLeaveRoom}
              className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700"
            >
              Leave Room
            </button>
          </div>
        </div>
      </header>

      <div className="flex flex-1 overflow-hidden">
        {/* Voice Chat Panel */}
        <div className="w-64 bg-white border-r">
          <div className="p-4">
            <h2 className="text-lg font-semibold">Participants</h2>
            <ul className="mt-4 space-y-2">
              {participants.map((participant) => (
                <li key={participant} className="flex items-center">
                  <span className="w-2 h-2 mr-2 bg-green-500 rounded-full"></span>
                  {participant}
                  {remoteStreams[participant] && (
                    <audio
                      autoPlay
                      ref={(audio) => {
                        if (audio && remoteStreams[participant]) {
                          audio.srcObject = remoteStreams[participant];
                        }
                      }}
                    />
                  )}
                </li>
              ))}
            </ul>
            <button
              onClick={toggleMute}
              className="w-full px-4 py-2 mt-4 text-sm font-medium text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
            >
              {isMuted ? 'Unmute' : 'Mute'}
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          <div className="flex items-center p-4 bg-white border-b">
            <select
              value={selectedLanguage}
              onChange={(e) => handleLanguageChange(e.target.value)}
              className="px-3 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
            >
              {LANGUAGES.map((lang) => (
                <option key={lang.id} value={lang.id}>
                  {lang.name}
                </option>
              ))}
            </select>
            <button
              onClick={handleRunCode}
              className="ml-4 px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-md hover:bg-green-700"
            >
              Run Code
            </button>
          </div>
          <div className="flex-1 p-4 overflow-auto">
            <Editor
              height="100%"
              language={selectedLanguage}
              value={code}
              onChange={handleCodeChange}
              theme="vs-dark"
              options={{
                minimap: { enabled: false },
                fontSize: 14,
                automaticLayout: true,
              }}
            />
          </div>
        </div>

        {/* Chat Panel */}
        <div className="w-80 bg-white border-l">
          <div className="flex flex-col h-full">
            <div className="flex-1 p-4 overflow-auto">
              {messages.map((msg, index) => (
                <div key={index} className="mb-4">
                  <span className="font-semibold">{msg.username}: </span>
                  <span>{msg.message}</span>
                </div>
              ))}
              <div ref={messagesEndRef} />
            </div>
            <form onSubmit={handleSendMessage} className="p-4 border-t">
              <div className="flex">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  className="flex-1 px-3 py-2 border rounded-l-md focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  placeholder="Type a message..."
                />
                <button
                  type="submit"
                  className="px-4 py-2 text-white bg-indigo-600 rounded-r-md hover:bg-indigo-700"
                >
                  Send
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Room;
