import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { io } from 'socket.io-client';
import Editor from '@monaco-editor/react';
import axios from 'axios';
import { LANGUAGE_VERSIONS } from '../constants';

const LANGUAGES = [
    { value: 'python', label: 'Python' },
    { value: 'javascript', label: 'JavaScript' },
    { value: 'java', label: 'Java' },
    { value: 'cpp', label: 'C++' },
    { value: 'csharp', label: 'C#' },
    { value: 'typescript', label: 'TypeScript' },
    { value: 'go', label: 'Go' },
    { value: 'rust', label: 'Rust' }
];

// Map language values to Monaco editor language ids
const LANGUAGE_MAPPING = {
    'python': 'python',
    'javascript': 'javascript',
    'java': 'java',
    'cpp': 'cpp',
    'csharp': 'csharp',
    'typescript': 'typescript',
    'go': 'go',
    'rust': 'rust'
};

// Initial code templates for each language
const CODE_TEMPLATES = {
    'python': '# Welcome to Python\nprint("Hello, World!")',
    'javascript': '// Welcome to JavaScript\nconsole.log("Hello, World!");',
    'java': '// Welcome to Java\npublic class Main {\n    public static void main(String[] args) {\n        System.out.println("Hello, World!");\n    }\n}',
    'cpp': '// Welcome to C++\n#include <iostream>\n\nint main() {\n    std::cout << "Hello, World!" << std::endl;\n    return 0;\n}',
    'csharp': '// Welcome to C#\nusing System;\n\nclass Program {\n    static void Main() {\n        Console.WriteLine("Hello, World!");\n    }\n}',
    'typescript': '// Welcome to TypeScript\nfunction greet(name: string): string {\n    return `Hello, ${name}!`;\n}\n\nconsole.log(greet("World"));',
    'go': '// Welcome to Go\npackage main\n\nimport "fmt"\n\nfunc main() {\n    fmt.Println("Hello, World!")\n}',
    'rust': '// Welcome to Rust\nfn main() {\n    println!("Hello, World!");\n}'
};

const Room = () => {
    const { roomId } = useParams();
    const navigate = useNavigate();
    const [username, setUsername] = useState('');
    const [code, setCode] = useState('');
    const [selectedLanguage, setSelectedLanguage] = useState('python');
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [isMuted, setIsMuted] = useState(true);
    const [output, setOutput] = useState('');
    const [error, setError] = useState('');
    const [isExecuting, setIsExecuting] = useState(false);
    const [participants, setParticipants] = useState([]);
    const [isConnected, setIsConnected] = useState(false);
    const [isVoiceEnabled, setIsVoiceEnabled] = useState(false);
    const [isSocketConnected, setIsSocketConnected] = useState(false);

    const socketRef = useRef();
    const localStreamRef = useRef();
    const peerConnectionsRef = useRef({});
    const messagesEndRef = useRef();
    const editorRef = useRef(null);

    // Function to handle editor mounting
    const handleEditorDidMount = (editor) => {
        editorRef.current = editor;
    };

    useEffect(() => {
        const storedUsername = localStorage.getItem('username');
        if (!storedUsername) {
            navigate('/');
            return;
        }
        setUsername(storedUsername);

        // Set initial code template
        setCode(CODE_TEMPLATES[selectedLanguage]);

        // Initialize socket connection
        socketRef.current = io('http://localhost:5001', {
            withCredentials: true,
            reconnection: true,
            reconnectionDelay: 1000,
            reconnectionDelayMax: 5000,
            reconnectionAttempts: Infinity
        });

        // Socket connection events
        socketRef.current.on('connect', () => {
            console.log('Socket connected');
            setIsSocketConnected(true);

            // Join room upon connection
            socketRef.current.emit('join', { room: roomId, username: storedUsername });
        });

        socketRef.current.on('disconnect', () => {
            console.log('Socket disconnected');
            setIsSocketConnected(false);
        });

        socketRef.current.on('connect_error', (error) => {
            console.error('Socket connection error:', error);
            setIsSocketConnected(false);
        });

        // Handle initial state
        socketRef.current.on('initial_state', (data) => {
            console.log('Received initial state:', data);
            if (data.code) setCode(data.code);
            if (data.language) setSelectedLanguage(data.language);
            if (data.participants) setParticipants(data.participants);
        });

        // Handle code updates
        socketRef.current.on('code_updated', (data) => {
            console.log('Code updated by:', data.username);
            if (data.username !== storedUsername) {
                setCode(data.code);
            }
        });

        // Handle language updates
        socketRef.current.on('language_updated', (data) => {
            console.log('Language updated by:', data.username);
            if (data.username !== storedUsername) {
                setSelectedLanguage(data.language);
            }
        });

        // Handle chat messages
        socketRef.current.on('new_message', (data) => {
            console.log('New message:', data);
            setMessages(prev => [...prev, data]);
        });

        // Handle user join/leave
        socketRef.current.on('user_joined', (data) => {
            console.log('User joined:', data.username);
            setMessages(prev => [...prev, { username: 'System', message: `${data.username} joined the room` }]);
            setParticipants(prev => {
                // Only add participant if they're not already in the list
                if (!prev.includes(data.username)) {
                    return [...prev, data.username];
                }
                return prev;
            });

            if (isVoiceEnabled && localStreamRef.current) {
                initializePeerConnection(data.username);
            }
        });

        socketRef.current.on('user_left', (data) => {
            console.log('User left:', data.username);
            setMessages(prev => [...prev, { username: 'System', message: `${data.username} left the room` }]);
            setParticipants(prev => prev.filter(user => user !== data.username));
            cleanupPeerConnection(data.username);
        });

        return () => {
            if (socketRef.current) {
                socketRef.current.emit('leave', { room: roomId, username: storedUsername });
                socketRef.current.disconnect();
            }
            cleanupAllPeerConnections();
        };
    }, [roomId, navigate]);

    // Effect for handling voice connections when isVoiceEnabled changes
    useEffect(() => {
        if (isVoiceEnabled && participants.length > 0 && isSocketConnected) {
            console.log('Setting up voice connections with participants:', participants);
            participants.forEach(participant => {
                if (participant !== username) {
                    initializePeerConnection(participant);
                }
            });
        }
    }, [isVoiceEnabled, participants, username, isSocketConnected]);

    // Effect to update code template when language changes
    useEffect(() => {
        if (!code || code === CODE_TEMPLATES[selectedLanguage]) {
            setCode(CODE_TEMPLATES[selectedLanguage]);
        }
    }, [selectedLanguage]);

    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    // Initialize WebRTC
    const initializeWebRTC = async () => {
        try {
            console.log("Initializing WebRTC...");
            // Get user media
            const stream = await navigator.mediaDevices.getUserMedia({
                audio: {
                    echoCancellation: true,
                    noiseSuppression: true,
                    autoGainControl: true
                }
            });

            console.log("User media acquired successfully");
            localStreamRef.current = stream;
            setIsVoiceEnabled(true);
            setIsConnected(true);
            setIsMuted(false); // Start unmuted by default

            // Set up WebRTC event handlers
            setupWebRTCEventHandlers();

            console.log("WebRTC initialized successfully");
        } catch (error) {
            console.error('Error initializing WebRTC:', error);
            alert("Error accessing microphone. Please make sure your microphone is connected and you've granted permission.");
            setIsVoiceEnabled(false);
            setIsConnected(false);
        }
    };

    // Create a new peer connection
    const createPeerConnection = (targetUsername) => {
        if (targetUsername === username) return null;
        console.log(`Creating peer connection with ${targetUsername}`);

        // Close existing connection if it exists
        if (peerConnectionsRef.current[targetUsername]) {
            peerConnectionsRef.current[targetUsername].close();
        }

        const pc = new RTCPeerConnection(configuration);
        peerConnectionsRef.current[targetUsername] = pc;

        // Log connection state changes
        pc.onconnectionstatechange = () => {
            console.log(`Connection state with ${targetUsername}: ${pc.connectionState}`);
            if (pc.connectionState === 'connected') {
                console.log(`Successfully connected to ${targetUsername}`);
            } else if (pc.connectionState === 'failed' || pc.connectionState === 'disconnected' || pc.connectionState === 'closed') {
                console.log(`Connection with ${targetUsername} is ${pc.connectionState}`);
                if (pc.connectionState === 'failed') {
                    // Attempt to reconnect
                    setTimeout(() => {
                        if (isVoiceEnabled && localStreamRef.current) {
                            cleanupPeerConnection(targetUsername);
                            initializePeerConnection(targetUsername);
                        }
                    }, 2000);
                }
            }
        };

        pc.oniceconnectionstatechange = () => {
            console.log(`ICE connection state with ${targetUsername}: ${pc.iceConnectionState}`);
            if (pc.iceConnectionState === 'failed') {
                // Try to restart ICE
                pc.restartIce();
            }
        };

        // Add local stream to the connection
        if (localStreamRef.current) {
            localStreamRef.current.getTracks().forEach(track => {
                console.log(`Adding track to peer connection with ${targetUsername}`, track);
                pc.addTrack(track, localStreamRef.current);
            });
        } else {
            console.error("Local stream not available when creating peer connection");
            return null;
        }

        // Handle ICE candidates
        pc.onicecandidate = (event) => {
            if (event.candidate) {
                console.log(`Sending ICE candidate to ${targetUsername}`, event.candidate);
                socketRef.current.emit('webrtc_ice', {
                    room: roomId,
                    candidate: event.candidate,
                    from: username,
                    to: targetUsername
                });
            }
        };

        // Handle incoming audio stream
        pc.ontrack = (event) => {
            console.log(`Received track from ${targetUsername}`, event.streams[0]);
            const audioEl = document.createElement('audio');
            audioEl.srcObject = event.streams[0];
            audioEl.id = `audio-${targetUsername}`;
            audioEl.autoplay = true;
            audioEl.controls = false;
            document.body.appendChild(audioEl);
        };

        return pc;
    };

    // Set up WebRTC event handlers
    const setupWebRTCEventHandlers = () => {
        // Handle incoming offer
        socketRef.current.on('webrtc_offer', async (data) => {
            if (data.from === username || (data.to && data.to !== username)) return;

            console.log(`Received offer from ${data.from}`, data.offer);

            const pc = createPeerConnection(data.from);
            if (!pc) return;

            try {
                await pc.setRemoteDescription(new RTCSessionDescription(data.offer));
                const answer = await pc.createAnswer();
                await pc.setLocalDescription(answer);

                console.log(`Sending answer to ${data.from}`, answer);
                socketRef.current.emit('webrtc_answer', {
                    room: roomId,
                    answer: answer,
                    from: username,
                    to: data.from
                });
            } catch (error) {
                console.error('Error handling offer:', error);
            }
        });

        // Handle incoming answer
        socketRef.current.on('webrtc_answer', async (data) => {
            if (data.from === username || (data.to && data.to !== username)) return;

            console.log(`Received answer from ${data.from}`, data.answer);

            const pc = peerConnectionsRef.current[data.from];
            if (pc) {
                try {
                    await pc.setRemoteDescription(new RTCSessionDescription(data.answer));
                    console.log(`Successfully set remote description for ${data.from}`);
                } catch (error) {
                    console.error('Error handling answer:', error);
                }
            }
        });

        // Handle ICE candidates
        socketRef.current.on('webrtc_ice', async (data) => {
            if (data.from === username || (data.to && data.to !== username)) return;

            console.log(`Received ICE candidate from ${data.from}`, data.candidate);

            const pc = peerConnectionsRef.current[data.from];
            if (pc) {
                try {
                    await pc.addIceCandidate(new RTCIceCandidate(data.candidate));
                    console.log(`Successfully added ICE candidate for ${data.from}`);
                } catch (error) {
                    console.error('Error handling ICE candidate:', error);
                }
            }
        });
    };

    // Initialize peer connection and send offer
    const initializePeerConnection = async (targetUsername) => {
        if (targetUsername === username) return;
        console.log(`Initializing peer connection with ${targetUsername}`);

        const pc = createPeerConnection(targetUsername);
        if (!pc) return;

        try {
            const offer = await pc.createOffer();
            await pc.setLocalDescription(offer);

            console.log(`Sending offer to ${targetUsername}`, offer);
            socketRef.current.emit('webrtc_offer', {
                room: roomId,
                offer: offer,
                from: username,
                to: targetUsername
            });
        } catch (error) {
            console.error('Error creating offer:', error);
        }
    };

    // Clean up peer connection
    const cleanupPeerConnection = (username) => {
        if (peerConnectionsRef.current[username]) {
            console.log(`Cleaning up peer connection with ${username}`);
            peerConnectionsRef.current[username].close();
            delete peerConnectionsRef.current[username];
        }

        // Remove audio element
        const audioEl = document.getElementById(`audio-${username}`);
        if (audioEl) {
            audioEl.remove();
        }
    };

    // Clean up all peer connections
    const cleanupAllPeerConnections = () => {
        console.log("Cleaning up all peer connections");
        Object.keys(peerConnectionsRef.current).forEach(cleanupPeerConnection);

        // Stop local media stream
        if (localStreamRef.current) {
            localStreamRef.current.getTracks().forEach(track => {
                track.stop();
            });
            localStreamRef.current = null;
        }

        // Remove all audio elements
        document.querySelectorAll('audio[id^="audio-"]').forEach(el => el.remove());
    };

    // Toggle voice chat
    const toggleVoiceChat = async () => {
        if (!isVoiceEnabled) {
            await initializeWebRTC();
        } else {
            cleanupAllPeerConnections();
            setIsVoiceEnabled(false);
            setIsConnected(false);
            setIsMuted(true);
        }
    };

    // Toggle mute
    const toggleMute = () => {
        if (localStreamRef.current) {
            localStreamRef.current.getAudioTracks().forEach(track => {
                track.enabled = !track.enabled;
            });
            setIsMuted(!isMuted);
        }
    };

    const handleCodeChange = (value) => {
        setCode(value);
        if (socketRef.current && socketRef.current.connected) {
            socketRef.current.emit('code_change', {
                room: roomId,
                code: value,
                username: username
            });
        }
    };

    const handleLanguageChange = (e) => {
        const newLanguage = e.target.value;
        setSelectedLanguage(newLanguage);
        if (socketRef.current && socketRef.current.connected) {
            socketRef.current.emit('language_change', {
                room: roomId,
                language: newLanguage,
                username: username
            });
        }
    };

    const handleSendMessage = (e) => {
        e.preventDefault();
        if (message.trim() && socketRef.current && socketRef.current.connected) {
            socketRef.current.emit('chat_message', {
                room: roomId,
                message: message.trim(),
                username: username
            });
            setMessage('');
        }
    };

    const executeCode = async () => {
        setIsExecuting(true);
        setOutput('');
        setError('');

        try {
            const response = await axios.post('https://emkc.org/api/v2/piston/execute', {
                language: selectedLanguage,
                version: LANGUAGE_VERSIONS[selectedLanguage],
                files: [
                    {
                        content: code
                    }
                ]
            });

            if (response.data.run) {
                setOutput(response.data.run.output);
                if (response.data.run.stderr) {
                    setError(response.data.run.stderr);
                }
            }
        } catch (err) {
            setError(err.response?.data?.error || 'Failed to execute code');
        } finally {
            setIsExecuting(false);
        }
    };

    return (
        <div className="flex h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white">
            {/* Left Panel - Code Editor */}
            <div className="w-2/3 flex flex-col">
                <div className="p-4 bg-gray-800/50 backdrop-blur-sm shadow-xl flex justify-between items-center border-b border-gray-700">
                    <div className="flex items-center space-x-4">
                        <span className="text-lg font-mono font-bold text-emerald-400">Room: {roomId}</span>
                        <select
                            value={selectedLanguage}
                            onChange={handleLanguageChange}
                            className="px-4 py-2 bg-gray-700 border border-emerald-400 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-emerald-400"
                        >
                            {LANGUAGES.map(lang => (
                                <option key={lang.value} value={lang.value}>
                                    {lang.label}
                                </option>
                            ))}
                        </select>
                        <div className={`ml-4 px-2 py-1 rounded-full ${isSocketConnected ? 'bg-emerald-500' : 'bg-red-500'} text-xs`}>
                            {isSocketConnected ? 'Connected' : 'Disconnected'}
                        </div>
                    </div>
                    <button
                        onClick={executeCode}
                        disabled={isExecuting}
                        className="px-4 py-2 bg-emerald-500 text-white rounded-lg hover:bg-emerald-600 disabled:opacity-50 font-bold transition-colors"
                    >
                        {isExecuting ? 'Executing...' : 'Run Code'}
                    </button>
                </div>
                <div className="flex-1">
                    <Editor
                        height="100%"
                        language={LANGUAGE_MAPPING[selectedLanguage]}
                        value={code}
                        onChange={handleCodeChange}
                        onMount={handleEditorDidMount}
                        theme="vs-dark"
                        options={{
                            minimap: { enabled: false },
                            fontSize: 14,
                            wordWrap: 'on',
                            automaticLayout: true,
                            fontFamily: 'Fira Code, monospace'
                        }}
                    />
                </div>
                <div className="p-4 bg-gray-800/50 backdrop-blur-sm border-t border-gray-700">
                    <h3 className="text-lg font-mono font-bold text-emerald-400 mb-2">Output</h3>
                    <div className="bg-gray-900 text-white p-4 rounded-lg font-mono text-sm h-32 overflow-auto border border-gray-700">
                        {output && <div className="text-green-400">{output}</div>}
                        {error && <div className="text-red-400">{error}</div>}
                    </div>
                </div>
            </div>

            {/* Right Panel - Chat and Voice */}
            <div className="w-1/3 flex flex-col border-l border-gray-700">
                <div className="p-4 bg-gray-800/50 backdrop-blur-sm shadow-xl">
                    <div className="flex justify-between items-center mb-4">
                        <div className="flex items-center space-x-2">
                            <div className={`w-2 h-2 rounded-full ${isConnected ? 'bg-emerald-500' : 'bg-red-500'}`}></div>
                            <span className="font-mono font-bold text-emerald-400">Voice Status</span>
                        </div>
                        <div className="flex space-x-2">
                            <button
                                onClick={toggleVoiceChat}
                                className={`px-4 py-2 rounded-lg font-bold ${
                                    isVoiceEnabled ? 'bg-emerald-500 hover:bg-emerald-600' : 'bg-gray-600 hover:bg-gray-700'
                                } text-white transition-colors`}
                            >
                                {isVoiceEnabled ? 'Disable Voice' : 'Enable Voice'}
                            </button>
                            {isVoiceEnabled && (
                                <button
                                    onClick={toggleMute}
                                    className={`px-4 py-2 rounded-lg font-bold ${
                                        isMuted ? 'bg-red-500 hover:bg-red-600' : 'bg-emerald-500 hover:bg-emerald-600'
                                    } text-white transition-colors`}
                                >
                                    {isMuted ? 'Unmute' : 'Mute'}
                                </button>
                            )}
                        </div>
                    </div>

                    <div className="mt-4">
                        <h3 className="font-mono font-bold text-emerald-400 mb-2">Participants ({participants.length})</h3>
                        <div className="bg-gray-900 rounded-lg p-2 max-h-24 overflow-y-auto">
                            {participants.map((participant, index) => (
                                <div key={index} className="py-1 px-2 rounded mb-1 bg-gray-800 flex items-center">
                                    <span className="font-mono">{participant}</span>
                                    {participant === username && <span className="ml-2 text-xs bg-emerald-500 px-2 py-0.5 rounded">You</span>}
                                </div>
                            ))}
                        </div>
                    </div>
                </div>

                <div className="flex-1 p-4 overflow-auto bg-gray-900/50 backdrop-blur-sm">
                    <h3 className="font-mono font-bold text-emerald-400 mb-2">Chat</h3>
                    <div className="space-y-2">
                        {messages.map((msg, index) => (
                            <div key={index} className={`p-2 rounded-lg ${msg.username === 'System' ? 'bg-gray-800/50' : 'bg-gray-800/90'}`}>
                                <span className={`font-bold ${msg.username === 'System' ? 'text-gray-400' : 'text-emerald-400'}`}>{msg.username}: </span>
                                <span>{msg.message}</span>
                            </div>
                        ))}
                        <div ref={messagesEndRef} />
                    </div>
                </div>

                <form onSubmit={handleSendMessage} className="p-4 bg-gray-800/50 backdrop-blur-sm border-t border-gray-700">
                    <div className="flex">
                        <input
                            type="text"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            placeholder="Type a message..."
                            className="flex-1 px-4 py-2 bg-gray-700 border border-gray-600 rounded-l-lg text-white focus:outline-none focus:ring-2 focus:ring-emerald-400"
                        />
                        <button
                            type="submit"
                            className="px-4 py-2 bg-emerald-500 text-white rounded-r-lg hover:bg-emerald-600 font-bold transition-colors"
                        >
                            Send
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Room;
